            <div class=\"form\">
            {{ form_label(form.plainPassword.first, 'Choisissez le mot de passe') }}
            {{ form_errors(form.plainPassword.first) }}
            {{ form_widget(form.plainPassword.first, {'attr': {'class':'form-control'}}) }}
            </div>
            <div class=\"form\">
            {{ form_label(form.plainPassword.second, 'Confirmer le mot de passe' ) }}
            {{ form_errors(form.plainPassword.second) }}
            {{ form_widget(form.plainPassword.second, {'attr': {'class':'form-control'}}) }}
            </div>
            <div class=\"row justify-content-center\">
                <div class=\"form bouton top\">
                   <button class=\"btn btn-secondary \" type=\"submit\" value=\"{{ 'resetting.reset.submit'|trans }}\">Enregistrer le nouveau mot de passe</button>
                </div>
            </div>
        {{ form_end(form) }}
        </div>
    </div>
</div>
{% endblock %}
